const axios = require('axios');
const data = {"resourceType":"AdverseEvent","id":"70791e9d-4941-4275-acce-ba75481c9c07","identifier":{"system":"http://acme.com/ids/patients/risks","value":"49476534"},"actuality":"actual","category":[{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/adverse-event-category","code":"product-use-error","display":"Product Use Error"}]}],"event":{"coding":[{"system":"http://snomed.info/sct","code":"304386008","display":"O/E - itchy rash"}],"text":"This was a mild rash on the left forearm"},"subject":{"reference":"Patient/example"},"date":"2017-01-29T20:34:56+08:00","seriousness":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/adverse-event-seriousness","code":"Non-serious","display":"Non-serious"}]},"severity":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/adverse-event-severity","code":"mild","display":"Mild"}]},"recorder":{"reference":"Practitioner/example"},"suspectEntity":[{"instance":{"reference":"Medication/example"}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:14.217+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/AdverseEvent',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

