const axios = require('axios');
const data = {"resourceType":"AppointmentResponse","id":"74c1c386-627c-475c-acbc-7f523e9a63ad","appointment":{"reference":"Appointment/example","display":"Brian MRI results discussion"},"actor":{"reference":"Patient/example","display":"Peter James Chalmers"},"participantStatus":"accepted","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:14.364+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/AppointmentResponse',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

