 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/bodystructure-example.json
const axios = require('axios');
const data = {"resourceType":"BodyStructure","id":"1a94f7d8-3ad4-4b52-b2cc-057e2a3ce5cc","identifier":[{"system":"http://goodhealth.org/bodystructure/identifiers","value":"12345"}],"location":{"coding":[{"system":"http://snomed.info/sct","code":"83418008","display":"Entire fetus (body structure)"}],"text":"Fetus"},"description":"EDD 1/1/2017 confirmation by LMP","patient":{"reference":"Patient/example"},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:14.551+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/BodyStructure',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

