const axios = require('axios');
const data = {"resourceType":"ClinicalImpression","id":"70d5ae73-8847-459e-a7b5-70655bd329c7","identifier":[{"value":"12345"}],"status":"completed","description":"This 26 yo male patient is brought into ER by ambulance after being involved in a motor vehicle accident","subject":{"reference":"Patient/example"},"encounter":{"reference":"Encounter/example"},"effectivePeriod":{"start":"2014-12-06T17:00:00+08:00","end":"2014-12-06T19:33:00+08:00"},"date":"2014-12-06T19:33:00+08:00","assessor":{"reference":"Practitioner/example"},"problem":[{"display":"MVA"}],"investigation":[{"code":{"text":"Initial Examination"},"item":[{"display":"deep laceration of the scalp (left temporo-occipital)"},{"display":"decreased level of consciousness"},{"display":"disoriented to time and place"},{"display":"restless"}]}],"summary":"provisional diagnoses of laceration of head and traumatic brain injury (TBI)","finding":[{"itemCodeableConcept":{"coding":[{"system":"http://hl7.org/fhir/sid/icd-9","code":"850.0"}]}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:15.022+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ClinicalImpression',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

