 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/codesystem-example.json
const axios = require('axios');
const data = {"resourceType":"CodeSystem","id":"3ff46f1c-d374-4550-b740-9a56156ab664","meta":{"profile":["http://hl7.org/fhir/StructureDefinition/shareablecodesystem"],"versionId":"1","lastUpdated":"2022-02-13T22:30:15.075+08:00"},"url":"http://hl7.org/fhir/CodeSystem/example","identifier":[{"system":"http://acme.com/identifiers/codesystems","value":"internal-cholesterol-inl"}],"version":"20160128","name":"ACMECholCodesBlood","title":"ACME Codes for Cholesterol in Serum/Plasma","status":"draft","experimental":true,"date":"2016-01-28T08:00:00+08:00","publisher":"Acme Co","contact":[{"name":"FHIR project team","telecom":[{"system":"url","value":"http://hl7.org/fhir"}]}],"description":"This is an example code system that includes all the ACME codes for serum/plasma cholesterol from v2.36.","caseSensitive":true,"content":"complete","filter":[{"code":"acme-plasma","description":"An internal filter used to select codes that are only used with plasma","operator":["="],"value":"the value of this filter is either 'true' or 'false'"}],"concept":[{"code":"chol-mmol","display":"SChol (mmol/L)","definition":"Serum Cholesterol, in mmol/L","designation":[{"use":{"system":"http://acme.com/config/fhir/codesystems/internal","code":"internal-label"},"value":"From ACME POC Testing"}]},{"code":"chol-mass","display":"SChol (mg/L)","definition":"Serum Cholesterol, in mg/L","designation":[{"use":{"system":"http://acme.com/config/fhir/codesystems/internal","code":"internal-label"},"value":"From Paragon Labs"}]},{"code":"chol","display":"SChol","definition":"Serum Cholesterol","designation":[{"use":{"system":"http://acme.com/config/fhir/codesystems/internal","code":"internal-label"},"value":"Obdurate Labs uses this with both kinds of units..."}]}]}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/CodeSystem',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

