const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/CommunicationRequest',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

