const axios = require('axios');
const data = {"resourceType":"CompartmentDefinition","id":"7e5d793f-4d39-45ef-b555-fba333d063b3","url":"http://hl7.org/fhir/CompartmentDefinition/example","name":"EXAMPLE","status":"draft","experimental":true,"date":"2017-02-24T08:00:00+08:00","publisher":"Health Level Seven International (FHIR Infrastructure)","contact":[{"name":"[string]","telecom":[{"system":"url","value":"http://hl7.org/fhir"}]}],"description":"The set of resources associated with a particular Device (example with Communication and CommunicationRequest resourses only).","useContext":[{"code":{"system":"http://terminology.hl7.org/CodeSystem/usage-context-type","code":"focus"},"valueCodeableConcept":{"coding":[{"system":"http://hl7.org/fhir/resource-types","code":"Device"}]}}],"purpose":"Provides an example of a FHIR compartment definition based on the Device resource type.","code":"Device","search":true,"resource":[{"code":"Communication","param":["sender","recipient"],"documentation":"The device used as the message sender and recipient"},{"code":"CommunicationRequest","param":["sender","recipient"],"documentation":"The device used as the message sender and recipient"}],"meta":{"versionId":"1","lastUpdated":"2022-02-13T22:30:15.183+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/CompartmentDefinition',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

