const axios = require('axios');
const config = {
    method: 'delete',
    url: 'http://burni.example.com/fhir/Composition/dfd2bfb7-95e0-4363-b344-e66871352ea0'
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

