const axios = require('axios');
const data = {"resourceType":"ConceptMap","id":"ccb8fb75-4d30-4665-ab87-300a0ba65318","url":"http://hl7.org/fhir/ConceptMap/101","identifier":{"system":"urn:ietf:rfc:3986","value":"urn:uuid:53cd62ee-033e-414c-9f58-3ca97b5ffc3b"},"version":"4.0.1","name":"FHIR-v3-Address-Use","title":"FHIR/v3 Address Use Mapping","status":"draft","experimental":true,"date":"2012-06-13T08:00:00+08:00","publisher":"HL7, Inc","contact":[{"name":"FHIR project team (example)","telecom":[{"system":"url","value":"http://hl7.org/fhir"}]}],"description":"A mapping between the FHIR and HL7 v3 AddressUse Code systems","useContext":[{"code":{"system":"http://terminology.hl7.org/CodeSystem/usage-context-type","code":"venue"},"valueCodeableConcept":{"text":"for CCDA Usage"}}],"jurisdiction":[{"coding":[{"system":"urn:iso:std:iso:3166","code":"US"}]}],"purpose":"To help implementers map from HL7 v3/CDA to FHIR","copyright":"Creative Commons 0","sourceUri":"http://hl7.org/fhir/ValueSet/address-use","targetUri":"http://terminology.hl7.org/ValueSet/v3-AddressUse","group":[{"source":"http://hl7.org/fhir/address-use","target":"http://terminology.hl7.org/CodeSystem/v3-AddressUse","element":[{"code":"home","display":"home","target":[{"code":"H","display":"home","equivalence":"equivalent"}]},{"code":"work","display":"work","target":[{"code":"WP","display":"work place","equivalence":"equivalent"}]},{"code":"temp","display":"temp","target":[{"code":"TMP","display":"temporary address","equivalence":"equivalent"}]},{"code":"old","display":"old","target":[{"code":"BAD","display":"bad address","equivalence":"disjoint","comment":"In the HL7 v3 AD, old is handled by the usablePeriod element, but you have to provide a time, there's no simple equivalent of flagging an address as old"}]}],"unmapped":{"mode":"fixed","code":"temp","display":"temp"}}],"meta":{"versionId":"1","lastUpdated":"2022-02-13T22:30:15.275+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ConceptMap',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

