 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/condition-example.json
const axios = require('axios');
const data = {"resourceType":"Condition","id":"88578a6c-5ef8-44be-a31c-01bfda5c7b17","clinicalStatus":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/condition-clinical","code":"active"}]},"verificationStatus":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/condition-ver-status","code":"confirmed"}]},"category":[{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/condition-category","code":"encounter-diagnosis","display":"Encounter Diagnosis"},{"system":"http://snomed.info/sct","code":"439401001","display":"Diagnosis"}]}],"severity":{"coding":[{"system":"http://snomed.info/sct","code":"24484000","display":"Severe"}]},"code":{"coding":[{"system":"http://snomed.info/sct","code":"39065001","display":"Burn of ear"}],"text":"Burnt Ear"},"bodySite":[{"coding":[{"system":"http://snomed.info/sct","code":"49521004","display":"Left external ear structure"}],"text":"Left Ear"}],"subject":{"reference":"Patient/example"},"onsetDateTime":"2012-05-24T08:00:00+08:00","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:15.316+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Condition',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

