const axios = require('axios');
const data = {"resourceType":"Consent","id":"95c1c170-803e-478a-939d-f55cd0e25258","status":"active","scope":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/consentscope","code":"patient-privacy"}]},"category":[{"coding":[{"system":"http://loinc.org","code":"59284-0"}]}],"patient":{"reference":"Patient/f001","display":"P. van de Heuvel"},"dateTime":"2016-05-11T08:00:00+08:00","organization":[{"reference":"Organization/f001"}],"sourceAttachment":{"title":"The terms of the consent in lawyer speak."},"policyRule":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActCode","code":"OPTIN"}]},"provision":{"period":{"start":"1964-01-01T08:00:00+08:00","end":"2016-01-01T08:00:00+08:00"}},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:15.347+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Consent',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

