const axios = require('axios');
const data = {"resourceType":"Device","id":"cecedf57-60df-46c2-99ba-81165311a0f0","identifier":[{"system":"http://goodcare.org/devices/id","value":"345675"}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:15.582+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Device',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

