 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/encounter-example.json
const axios = require('axios');
const data = {"resourceType":"Encounter","id":"8843e808-0938-4226-9524-6d3153851685","status":"in-progress","class":{"system":"http://terminology.hl7.org/CodeSystem/v3-ActCode","code":"IMP","display":"inpatient encounter"},"subject":{"reference":"Patient/example"},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:15.867+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Encounter',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

