const axios = require('axios');
const data = {"resourceType":"Endpoint","id":"232c00e8-0b9c-413d-aa7c-81d2221459f2","identifier":[{"system":"http://example.org/enpoint-identifier","value":"epcp12"}],"status":"active","connectionType":{"system":"http://terminology.hl7.org/CodeSystem/endpoint-connection-type","code":"hl7-fhir-rest"},"name":"Health Intersections CarePlan Hub","managingOrganization":{"reference":"Organization/hl7"},"contact":[{"system":"email","value":"endpointmanager@example.org","use":"work"}],"period":{"start":"2014-09-01T08:00:00+08:00"},"payloadType":[{"coding":[{"system":"http://hl7.org/fhir/resource-types","code":"CarePlan"}]}],"payloadMimeType":["application/fhir+xml"],"address":"http://fhir3.healthintersections.com.au/open/CarePlan","header":["bearer-code BASGS534s4"],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:15.892+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Endpoint',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

