 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/evidencevariable-example.json
const axios = require('axios');
const data = {"resourceType":"EvidenceVariable","id":"31e98060-96fc-45b8-b0bd-a5ec79f6dfa2","status":"draft","characteristic":[{"definitionCodeableConcept":{"text":"Diabetic patients over 65"}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:16.078+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/EvidenceVariable',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

