const axios = require('axios');
const data = {"resourceType":"FamilyMemberHistory","id":"b2aa2e8e-e333-490b-ba71-590eb1956b45","identifier":[{"value":"12345"}],"instantiatesUri":["http://example.org/family-member-history-questionnaire"],"status":"completed","patient":{"reference":"Patient/example","display":"Peter Patient"},"date":"2011-03-18T08:00:00+08:00","relationship":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/v3-RoleCode","code":"FTH","display":"father"}]},"sex":{"coding":[{"system":"http://hl7.org/fhir/administrative-gender","code":"male","display":"Male"}]},"condition":[{"code":{"coding":[{"system":"http://snomed.info/sct","code":"315619001","display":"Myocardial Infarction"}],"text":"Heart Attack"},"contributedToDeath":true,"onsetAge":{"value":74,"unit":"yr","system":"http://unitsofmeasure.org","code":"a"},"note":[{"text":"Was fishing at the time. At least he went doing someting he loved."}]}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:16.404+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/FamilyMemberHistory',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

