 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/flag-example.json
const axios = require('axios');
const data = {"resourceType":"Flag","id":"2b850583-af01-41bf-b94d-87953e4d88b6","identifier":[{"value":"12345"}],"status":"inactive","category":[{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/flag-category","code":"safety","display":"Safety"}],"text":"Safety"}],"code":{"coding":[{"system":"http://example.org/local","code":"bigdog","display":"Big dog"}],"text":"Patient has a big dog at his home. Always always wear a suit of armor or take other active counter-measures"},"subject":{"reference":"Patient/example","display":"Peter Patient"},"period":{"start":"2015-01-17T08:00:00+08:00","end":"2016-12-01T08:00:00+08:00"},"author":{"reference":"Practitioner/example","display":"Nancy Nurse"},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:16.436+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Flag',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

