const axios = require('axios');
const data = {"resourceType":"Goal","id":"627873bb-ae13-42ea-be95-437778c6a065","identifier":[{"value":"123"}],"lifecycleStatus":"on-hold","category":[{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/goal-category","code":"dietary"}]}],"priority":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/goal-priority","code":"high-priority","display":"High Priority"}],"text":"high"},"description":{"text":"Target weight is 160 to 180 lbs."},"subject":{"reference":"Patient/example","display":"Peter James Chalmers"},"startDate":"2015-04-05","target":[{"measure":{"coding":[{"system":"http://loinc.org","code":"3141-9","display":"Weight Measured"}]},"detailRange":{"low":{"value":160,"unit":"lbs","system":"http://unitsofmeasure.org","code":"[lb_av]"},"high":{"value":180,"unit":"lbs","system":"http://unitsofmeasure.org","code":"[lb_av]"}},"dueDate":"2016-04-05"}],"statusDate":"2016-02-14","statusReason":"Patient wants to defer weight loss until after honeymoon.","expressedBy":{"reference":"Patient/example","display":"Peter James Chalmers"},"addresses":[{"display":"obesity condition"}],"outcomeReference":[{"reference":"Observation/example","display":"Body Weight Measured"}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:16.469+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Goal',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

