const axios = require('axios');
const data = {"resourceType":"ImmunizationEvaluation","id":"f9d11117-55e4-40f5-bb30-dfd92ac2bd3a","identifier":[{"system":"urn:ietf:rfc:3986","value":"urn:oid:1.3.6.1.4.1.21367.2005.3.7.1234"}],"status":"completed","patient":{"reference":"Patient/example"},"date":"2013-01-10T08:00:00+08:00","authority":{"reference":"Organization/hl7"},"targetDisease":{"coding":[{"system":"http://snomed.info/sct","code":"1857005"}]},"immunizationEvent":{"reference":"Immunization/example"},"doseStatus":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/immunization-evaluation-dose-status","code":"valid","display":"Valid"}]},"series":"Vaccination Series 1","doseNumberPositiveInt":1,"seriesDosesPositiveInt":3,"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:16.724+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ImmunizationEvaluation',
    headers: { 
        'Content-Type': 'application/fhir+json'
