const data = {"resourceType":"ImplementationGuide","id":"54b17200-d58a-40ab-a2ae-bdf044be7bb9","url":"http://hl7.org/fhir/us/daf","version":"0","name":"Data Access Framework (DAF)","status":"draft","experimental":false,"date":"2015-01-01T08:00:00+08:00","publisher":"ONC / HL7 Joint project","contact":[{"name":"ONC","telecom":[{"system":"url","value":"http://www.healthit.gov"}]},{"name":"HL7","telecom":[{"system":"url","value":"http://hl7.org/fhir"}]}],"description":"The Data Access Framework (DAF) Initiative leverages the HL7 FHIR standards to standardize access to Meaningful Use Stage 2 structured information both within the organization and from external organizations","jurisdiction":[{"coding":[{"system":"urn:iso:std:iso:3166","code":"US"}]}],"copyright":"Published by ONC under the standard FHIR license (CC0)","packageId":"hl7.fhir.us.daf","license":"CC0-1.0","fhirVersion":["4.0.1"],"dependsOn":[{"uri":"http://hl7.org/fhir/ImplementationGuide/uscore"}],"global":[{"type":"Patient","profile":"http://hl7.org/fhir/us/core/StructureDefinition/patient"}],"definition":{"grouping":[{"name":"test","description":"Base package (not broken up into multiple packages)"}],"resource":[{"reference":{"reference":"Patient/test"},"name":"Test Example","description":"A test example to show how an implementation guide works","exampleCanonical":"http://hl7.org/fhir/us/core/StructureDefinition/patient"}],"page":{"nameUrl":"patient-example.html","title":"Example Patient Page","generation":"html","page":[{"nameUrl":"list.html","title":"Value Set Page","generation":"html"}]},"parameter":[{"code":"apply","value":"version"}]},"manifest":{"rendering":"http://hl7.org/fhir/us/daf","resource":[{"reference":{"reference":"Patient/test"},"exampleCanonical":"http://hl7.org/fhir/us/core/StructureDefinition/patient","relativePath":"patient-test.html#patient-test"}],"page":[{"name":"patient-test.html","title":"Test Patient Example","anchor":["patient-test","tx","uml"]}],"image":["fhir.png"],"other":["fhir.css"]},"meta":{"versionId":"1","lastUpdated":"2022-02-13T22:30:16.801+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ImplementationGuide',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

