const axios = require('axios');
const data = {"resourceType":"InsurancePlan","id":"49661cc3-8a11-4fa7-9efc-983e7fc9730a","name":"foo","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:16.845+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/InsurancePlan',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

