const axios = require('axios');
const data = {"resourceType":"Invoice","id":"0b6c657c-f776-425f-9205-aab1ddf27b58","identifier":[{"system":"http://myHospital.org/Invoices","value":"654321"}],"status":"issued","subject":{"reference":"Patient/example"},"date":"2017-01-25T15:00:00+08:00","participant":[{"role":{"coding":[{"system":"http://snomed.info/sct","code":"17561000","display":"Cardiologist"}]},"actor":{"reference":"Practitioner/example"}}],"issuer":{"identifier":{"system":"http://myhospital/NamingSystem/departments","value":"CARD_INTERMEDIATE_CARE"}},"account":{"reference":"Account/example"},"totalNet":{"value":40,"currency":"EUR"},"totalGross":{"value":48,"currency":"EUR"},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:16.878+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Invoice',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

