const data = {"resourceType":"Location","id":"6885fde7-86e3-4d72-b958-63ae9a1b7498","identifier":[{"value":"B1-S.F2"}],"status":"active","name":"South Wing, second floor","alias":["BU MC, SW, F2","Burgers University Medical Center, South Wing, second floor"],"description":"Second floor of the Old South Wing, formerly in use by Psychiatry","mode":"instance","telecom":[{"system":"phone","value":"2328","use":"work"},{"system":"fax","value":"2329","use":"work"},{"system":"email","value":"second wing admissions"},{"system":"url","value":"http://sampleorg.com/southwing","use":"work"}],"address":{"use":"work","line":["Galapagosweg 91, Building A"],"city":"Den Burg","postalCode":"9105 PZ","country":"NLD"},"physicalType":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/location-physical-type","code":"wi","display":"Wing"}]},"position":{"longitude":-83.6945691,"latitude":42.25475478,"altitude":0},"managingOrganization":{"reference":"Organization/f001"},"endpoint":[{"reference":"Endpoint/example"}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:17.011+08:00"}}
