const axios = require('axios');
const data = {"resourceType":"MedicationKnowledge","id":"cc90be2c-df4f-41aa-adfb-acbef0aca868","contained":[{"resourceType":"Organization","id":"org4","name":"Pfizer Laboratories Div Pfizer Inc"}],"code":{"coding":[{"system":"http://hl7.org/fhir/sid/ndc","code":"0069-2587-10","display":"Vancomycin Hydrochloride (VANCOMYCIN HYDROCHLORIDE)"}]},"status":"active","manufacturer":{"reference":"#org4"},"doseForm":{"coding":[{"system":"http://snomed.info/sct","code":"385219001","display":"Injection Solution (qualifier value)"}]},"amount":{"value":50,"unit":"mg/ml"},"synonym":["Vancomycin Hydrochloride (VANCOMYCIN HYDROCHLORIDE)"],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:17.642+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/MedicationKnowledge',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

