 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/medicinalproduct-example.json
const axios = require('axios');
const data = {"resourceType":"MedicinalProduct","id":"314567ce-b84f-4e40-99a2-c28627ac9825","identifier":[{"system":"http://ema.europa.eu/example/MPID","value":"{mpid}"}],"productClassification":[{"coding":[{"system":"http://ema.europa.eu/example/WHOAnatomicalTherapeuticChemicalATCClassificationSystem","code":"WHOAnatomicalTherapeuticChemicalATCClassificationSystem|B01AF02"}]}],"attachedDocument":[{"reference":"DocumentReference/example"}],"masterFile":[{"reference":"DocumentReference/example"}],"name":[{"productName":"Equilidem 2.5 mg film-coated tablets","namePart":[{"part":"Equilidem","type":{"code":"INV"}},{"part":"2.5 mg","type":{"code":"STR"}},{"part":"film-coated tablets","type":{"code":"FRM"}}],"countryLanguage":[{"country":{"coding":[{"system":"http://ema.europa.eu/example/countryCode","code":"EU"}]},"jurisdiction":{"coding":[{"system":"http://ema.europa.eu/example/jurisdictionCode","code":"EU"}]},"language":{"coding":[{"system":"http://ema.europa.eu/example/languageCode","code":"EN"}]}}]}],"manufacturingBusinessOperation":[{"operationType":{"coding":[{"system":"http://ema.europa.eu/example/manufacturingOperationType","code":"Batchrelease"}]},"authorisationReferenceNumber":{"system":"http://ema.europa.eu/example/manufacturingAuthorisationReferenceNumber","value":"1324TZ"},"effectiveDate":"2013-03-15T08:00:00+08:00","manufacturer":[{"reference":"Organization/example"}],"regulator":{"reference":"Organization/example"}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:17.817+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/MedicinalProduct',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

