 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/medicinalproductindication-example.json
const axios = require('axios');
const data = {"resourceType":"MedicinalProductIndication","id":"61904286-cdc0-4700-9c7a-d5f3d9bad827","diseaseSymptomProcedure":{"coding":[{"system":"http://ema.europa.eu/example/indicationasdisease-symptom-procedure","code":"Venousthromboembolismprophylaxis"}],"text":"Prevention of venous thromboembolic events (VTE) in adult patients who have undergone elective hip\\nor knee replacement surgery.\\nPrevention of stroke and systemic embolism in adult patients with non-valvular atrial fibrillation\\n(NVAF), with one or more risk factors, such as prior stroke or transient ischaemic attack (TIA); age\\n≥ 75 years; hypertension; diabetes mellitus; symptomatic heart failure (NYHA Class ≥ II).\\nTreatment of deep vein thrombosis (DVT) and pulmonary embolism (PE), and prevention of recurrent\\nDVT and PE in adults (see section 4.4 for haemodynamically unstable PE patients)."},"comorbidity":[{"coding":[{"system":"http://ema.europa.eu/example/comorbidity","code":"Hipsurgery"}]}],"intendedEffect":{"coding":[{"system":"http://ema.europa.eu/example/intendedeffect","code":"PRYLX"}]},"population":[{"ageRange":{"low":{"value":18,"unit":"a"}}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:17.924+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/MedicinalProductIndication',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

