 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/namingsystem-example.json
const axios = require('axios');
const data = {"resourceType":"NamingSystem","id":"94c7a0a9-134d-4908-9a5a-249ece75c67d","name":"SNOMED CT","status":"active","kind":"codesystem","date":"2014-12-13T08:00:00+08:00","publisher":"HL7 International on behalf of IHTSDO","contact":[{"name":"FHIR project team","telecom":[{"system":"url","value":"http://hl7.org/fhir"}]}],"responsible":"IHTSDO & affiliates","description":"SNOMED CT is a concept-based, scientifically validated terminology that provides a unique and permanent concept identifier that can be included in multiple HL7 data types including CD and CE.  The concepts are managed to avoid \"semantic drift\" so the meaning remains constant.  If the concept is found to be ambiguous or the meaning changes, the concept is inactivated but still retained and the identifier is never reused.    SNOMED CT's concepts are interrelated hierarchically and using description logic.    SNOMED CT concepts have a unique \"fully-specified name\", a preferred term, and, optionally, synonyms.  The description languages include English and Spanish.","uniqueId":[{"type":"oid","value":"2.16.840.1.113883.6.96"},{"type":"uri","value":"http://snomed.info/sct","preferred":true}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.277+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/NamingSystem',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

