const data = {"resourceType":"NutritionOrder","id":"6120c0ef-f474-4d52-a34f-365a2fd65002","identifier":[{"system":"http://goodhealthhospital.org/nutrition-requests","value":"123"}],"status":"active","intent":"order","patient":{"reference":"Patient/example","display":"Peter Chalmers"},"encounter":{"reference":"Encounter/example","display":"Inpatient"},"dateTime":"2014-09-17T08:00:00+08:00","orderer":{"reference":"Practitioner/example","display":"Dr Adam Careful"},"allergyIntolerance":[{"reference":"AllergyIntolerance/example","display":"Cashew Nuts"}],"foodPreferenceModifier":[{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/diet","code":"dairy-free"}]}],"excludeFoodModifier":[{"coding":[{"system":"http://snomed.info/sct","version":"20140730","code":"227493005","display":"Cashew Nut"}]}],"oralDiet":{"type":[{"coding":[{"system":"http://snomed.info/sct","code":"386619000","display":"Low sodium diet"},{"system":"http://goodhealthhospital.org/diet-type-codes","code":"1040","display":"Low Sodium Diet"}],"text":"Low sodium diet"},{"coding":[{"system":"http://snomed.info/sct","code":"226208002","display":"Fluid restricted diet"},{"system":"http://goodhealthhospital.org/diet-type-codes","code":"1040","display":"Fluid restricted diet"}],"text":"Fluid restricted diet"}],"nutrient":[{"modifier":{"coding":[{"system":"http://snomed.info/sct","code":"39972003","display":"Sodium"}]},"amount":{"value":2,"unit":"grams","system":"http://unitsofmeasure.org","code":"g"}},{"modifier":{"coding":[{"system":"http://snomed.info/sct","code":"33463005","display":"Fluid"}]},"amount":{"value":1500,"unit":"milliliter","system":"http://unitsofmeasure.org","code":"mL"}}],"instruction":"Starting on 2/10 breakfast, maximum 400 ml fluids per meal"},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.309+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/NutritionOrder',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

