const data = {"resourceType":"Observation","id":"a10bfc81-86c9-49b4-8a68-9226be3d1215","status":"final","category":[{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/observation-category","code":"vital-signs","display":"Vital Signs"}]}],"code":{"coding":[{"system":"http://loinc.org","code":"29463-7","display":"Body Weight"},{"system":"http://loinc.org","code":"3141-9","display":"Body weight Measured"},{"system":"http://snomed.info/sct","code":"27113001","display":"Body weight"},{"system":"http://acme.org/devices/clinical-codes","code":"body-weight","display":"Body Weight"}]},"subject":{"reference":"Patient/example"},"encounter":{"reference":"Encounter/example"},"effectiveDateTime":"2016-03-28T08:00:00+08:00","valueQuantity":{"value":185,"unit":"lbs","system":"http://unitsofmeasure.org","code":"[lb_av]"},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.354+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Observation',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

