 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/observationdefinition-example.json
const axios = require('axios');
const data = {"resourceType":"ObservationDefinition","id":"c99dbe7d-1dd2-4e32-b0f1-f4cb588d197d","code":{"coding":[{"system":"http://loinc.org","code":"15074-8","display":"Glucose [Moles/volume] in Blood"}]},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.385+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ObservationDefinition',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

