 //example from: https://chinlinlee.github.io/Burni/assets/FHIR/fhir-resource-examples/operationoutcome-example.json
const axios = require('axios');
const data = {"resourceType":"OperationOutcome","id":"3f6f78af-80a1-4be3-83a9-50f0299cf62c","issue":[{"severity":"error","code":"code-invalid","details":{"text":"The code \"W\" is not known and not legal in this context"},"diagnostics":"Acme.Interop.FHIRProcessors.Patient.processGender line 2453","location":["/f:Patient/f:gender"],"expression":["Patient.gender"]}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.447+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/OperationOutcome',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

