const axios = require('axios');
const data = {"resourceType":"Organization","id":"0d1fff8c-ffe4-4493-ba3a-73346bca2e98","name":"Health Level Seven International","alias":["HL7 International"],"telecom":[{"system":"phone","value":"(+1) 734-677-7777"},{"system":"fax","value":"(+1) 734-677-6622"},{"system":"email","value":"hq@HL7.org"}],"address":[{"line":["3300 Washtenaw Avenue, Suite 227"],"city":"Ann Arbor","state":"MI","postalCode":"48104","country":"USA"}],"endpoint":[{"reference":"Endpoint/example"}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.477+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Organization',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

