const data = {"resourceType":"Parameters","parameter":[{"name":"exact","valueBoolean":true},{"name":"property","part":[{"name":"code","valueCode":"focus"},{"name":"value","valueCode":"top"}]},{"name":"patient","resource":{"resourceType":"Patient","id":"example","name":[{"use":"official","family":"Chalmers","given":["Peter","James"]}]}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.545+08:00"},"id":"fe616661-5533-4752-90c2-7a2ac4a51d4b"}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Parameters',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

