const axios = require('axios');
const data = {"resourceType":"Person","id":"649544f9-f3ca-4f86-8af3-5957ca28f0eb","identifier":[{"use":"usual","type":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/v2-0203","code":"MR"}]},"system":"urn:oid:1.2.36.146.595.217.0.1","value":"12345","period":{"start":"2001-05-06T08:00:00+08:00"},"assigner":{"display":"Acme Healthcare"}}],"name":[{"use":"official","family":"Chalmers","given":["Peter","James"]},{"use":"usual","given":["Jim"]}],"telecom":[{"use":"home"},{"system":"phone","value":"(03) 5555 6473","use":"work"},{"system":"email","value":"Jim@example.org","use":"home"}],"gender":"male","birthDate":"1974-12-25","address":[{"use":"home","line":["534 Erewhon St"],"city":"PleasantVille","state":"Vic","postalCode":"3999"}],"active":true,"link":[{"target":{"reference":"RelatedPerson/peter","display":"Peter Chalmers"}},{"target":{"reference":"Patient/example","display":"Peter Chalmers"}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.710+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Person',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

