const data = {"resourceType":"Practitioner","id":"fc7fed26-7022-4647-8e32-0e1778653df8","identifier":[{"system":"http://www.acme.org/practitioners","value":"23"}],"active":true,"name":[{"family":"Careful","given":["Adam"],"prefix":["Dr"]}],"address":[{"use":"home","line":["534 Erewhon St"],"city":"PleasantVille","state":"Vic","postalCode":"3999"}],"qualification":[{"identifier":[{"system":"http://example.org/UniversityIdentifier","value":"12345"}],"code":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/v2-0360/2.7","code":"BS","display":"Bachelor of Science"}],"text":"Bachelor of Science"},"period":{"start":"1995-01-01T08:00:00+08:00"},"issuer":{"display":"Example University"}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.814+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Practitioner',
    headers: { 
        'Content-Type': 'application/fhir+json'
