const data = {"resourceType":"Provenance","id":"7e67e3aa-7872-41bc-ae0d-38cf9e9668ac","target":[{"reference":"Procedure/example/_history/1"}],"occurredPeriod":{"start":"2015-06-27T08:00:00+08:00","end":"2015-06-28T08:00:00+08:00"},"recorded":"2015-06-27T06:39:24.000+08:00","policy":["http://acme.com/fhir/Consent/25"],"location":{"reference":"Location/1"},"reason":[{"coding":[{"system":"http://snomed.info/sct","code":"3457005","display":"Referral"}]}],"agent":[{"type":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ParticipationType","code":"AUT"}]},"who":{"reference":"Practitioner/xcda-author"}},{"type":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ParticipationType","code":"DEV"}]},"who":{"reference":"Device/software"}}],"entity":[{"role":"source","what":{"reference":"DocumentReference/example","display":"CDA Document in XDS repository"}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:18.927+08:00"}}
