const axios = require('axios');
const data = {"resourceType":"ResearchElementDefinition","id":"a281e987-cdda-4b72-b822-5ce3b12d46a2","status":"draft","type":"population","characteristic":[{"definitionCodeableConcept":{"text":"Diabetic patients over 65"}}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.101+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ResearchElementDefinition',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

