const axios = require('axios');
const data = {"resourceType":"ResearchStudy","id":"c9d62f37-4ec7-4acc-aabd-3258537ea64e","status":"completed","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.136+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ResearchStudy',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

