const axios = require('axios');
const data = {"resourceType":"ResearchSubject","id":"2dd3f955-4929-4222-8a52-09c9f6c7f8e9","identifier":[{"type":{"text":"Subject id"},"system":"http://example.org/studysubjectids","value":"123"}],"status":"candidate","study":{"reference":"ResearchStudy/example"},"individual":{"reference":"Patient/example"},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.168+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ResearchSubject',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

