const axios = require('axios');
const data = {"resourceType":"Schedule","id":"ee94628b-e503-4c20-ab69-10ff496cc2a8","identifier":[{"use":"usual","system":"http://example.org/scheduleid","value":"45"}],"active":true,"serviceCategory":[{"coding":[{"code":"17","display":"General Practice"}]}],"serviceType":[{"coding":[{"code":"57","display":"Immunization"}]}],"specialty":[{"coding":[{"code":"408480009","display":"Clinical immunology"}]}],"actor":[{"reference":"Location/1","display":"Burgers UMC, South Wing, second floor"}],"planningHorizon":{"start":"2013-12-25T17:15:00+08:00","end":"2013-12-25T17:30:00+08:00"},"comment":"The slots attached to this schedule should be specialized to cover immunizations within the clinic","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.311+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Schedule',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

