const axios = require('axios');
const data = {"resourceType":"SearchParameter","id":"3581bdaa-374d-4ff2-8e7f-3e4604fc5c7e","url":"http://hl7.org/fhir/SearchParameter/example","version":"1","name":"ID-SEARCH-PARAMETER","derivedFrom":"http://hl7.org/fhir/SearchParameter/Resource-id","status":"draft","experimental":true,"date":"2013-10-23T08:00:00+08:00","publisher":"Health Level Seven International (FHIR Infrastructure)","contact":[{"name":"[string]","telecom":[{"system":"url","value":"http://hl7.org/fhir"}]}],"description":"Search by resource identifier - e.g. same as the read interaction, but can return included resources","useContext":[{"code":{"system":"http://terminology.hl7.org/CodeSystem/usage-context-type","code":"focus"},"valueCodeableConcept":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/variant-state","code":"positive"}]}}],"jurisdiction":[{"coding":[{"system":"urn:iso:std:iso:3166","code":"US","display":"United States of America (the)"}]}],"purpose":"Need to search by identifier for various infrastructural cases - mainly retrieving packages, and matching as part of a chain","code":"_id","base":["Resource"],"type":"token","expression":"id","xpath":"f:*\\f:id","xpathUsage":"normal","comparator":["eq"],"meta":{"versionId":"1","lastUpdated":"2022-02-13T22:30:19.359+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/SearchParameter',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

