const data = {"resourceType":"Slot","id":"42f7dda3-dd27-48e5-8c2c-dfdf3c049130","serviceCategory":[{"coding":[{"code":"17","display":"General Practice"}]}],"serviceType":[{"coding":[{"code":"57","display":"Immunization"}]}],"specialty":[{"coding":[{"code":"408480009","display":"Clinical immunology"}]}],"appointmentType":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/v2-0276","code":"WALKIN","display":"A previously unscheduled walk-in visit"}]},"schedule":{"reference":"Schedule/example"},"status":"free","start":"2013-12-25T17:15:00.000+08:00","end":"2013-12-25T17:30:00.000+08:00","comment":"Assessments should be performed before requesting appointments in this slot.","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.417+08:00"}}
