const axios = require('axios');
const data = {"resourceType":"Specimen","id":"537bd19c-fa87-4e8e-a11f-0399f26790f3","contained":[{"resourceType":"Substance","id":"hep","code":{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/v3-EntityCode","code":"HEPL"}]}}],"identifier":[{"system":"http://ehr.acme.org/identifiers/collections","value":"23234352356"}],"accessionIdentifier":{"system":"http://lab.acme.org/specimens/2011","value":"X352356"},"status":"available","type":{"coding":[{"system":"http://snomed.info/sct","code":"122555007","display":"Venous blood specimen"}]},"subject":{"reference":"Patient/example","display":"Peter Patient"},"receivedTime":"2011-03-04T15:03:00+08:00","request":[{"reference":"ServiceRequest/example"}],"container":[{"identifier":[{"value":"48736-15394-75465"}],"description":"Green Gel tube","type":{"text":"Vacutainer"},"capacity":{"value":10,"unit":"mL"},"specimenQuantity":{"value":6,"unit":"mL"},"additiveReference":{"reference":"#hep"}}],"note":[{"text":"Specimen is grossly lipemic"}],"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.450+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Specimen',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

