const data = {"resourceType":"Substance","id":"af3354f6-5c79-4a01-858a-88a80b98a684","identifier":[{"system":"http://acme.org/identifiers/substances","value":"1463"}],"status":"active","category":[{"coding":[{"system":"http://terminology.hl7.org/CodeSystem/substance-category","code":"allergen","display":"Allergen"}]}],"code":{"text":"apitoxin (Honey Bee Venom)"},"meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.654+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/Substance',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

