const axios = require('axios');
const data = {"resourceType":"SubstanceReferenceInformation","id":"52fb9e2d-54b5-4b4b-a9e6-86ca0bd69514","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.751+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/SubstanceReferenceInformation',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

