const axios = require('axios');
const data = {"resourceType":"SubstanceSourceMaterial","id":"909d5ce2-c9d0-4815-98f4-1576a3541cda","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:19.773+08:00"}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/SubstanceSourceMaterial',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

