const axios = require('axios');
const data = {"resourceType":"ValueSet","id":"1f36e263-59b0-4758-8b19-c999aa99e7a8","meta":{"profile":["http://hl7.org/fhir/StructureDefinition/shareablevalueset"],"versionId":"1","lastUpdated":"2022-02-13T22:30:20.089+08:00"},"url":"http://hl7.org/fhir/ValueSet/example-extensional","identifier":[{"system":"http://acme.com/identifiers/valuesets","value":"loinc-cholesterol-int"}],"version":"20150622","name":"LOINC Codes for Cholesterol in Serum/Plasma","status":"draft","experimental":true,"date":"2015-06-22T08:00:00+08:00","publisher":"HL7 International","contact":[{"name":"FHIR project team","telecom":[{"system":"url","value":"http://hl7.org/fhir"}]}],"description":"This is an example value set that includes all the LOINC codes for serum/plasma cholesterol from v2.36.","useContext":[{"code":{"system":"http://terminology.hl7.org/CodeSystem/usage-context-type","code":"age"},"valueQuantity":{"value":18,"comparator":">","unit":"yrs","system":"http://unitsofmeasure.org","code":"a"}}],"jurisdiction":[{"coding":[{"system":"urn:iso:std:iso:3166","code":"US"}]}],"purpose":"This value set was published by ACME Inc in order to make clear which codes are used for Cholesterol by AcmeClinicals (Adult Ambulatory care support in USA)","copyright":"This content from LOINC ® is copyright © 1995 Regenstrief Institute, Inc. and the LOINC Committee, and available at no cost under the license at http://loinc.org/terms-of-use.","compose":{"lockedDate":"2012-06-13","inactive":true,"include":[{"system":"http://loinc.org","version":"2.36","concept":[{"code":"14647-2","display":"Cholesterol [Moles/Volume]"},{"code":"2093-3","display":"Cholesterol [Mass/Volume]"},{"code":"35200-5","display":"Cholesterol [Mass Or Moles/Volume]"},{"code":"9342-7","display":"Cholesterol [Percentile]"}]}]}}
const config = {
    method: 'post',
    url: 'http://burni.example.com/fhir/ValueSet',
    headers: { 
        'Content-Type': 'application/fhir+json'
    },
    data: data
};

axios(config)
.then(function (response) {
    console.log(JSON.stringify(response.data));
})
.catch(function (error) {
    console.log(error);
});

