const data = {"resourceType":"VerificationResult","id":"a29fa342-73f0-4e26-b460-b4a5577ed829","status":"attested","meta":{"tag":[{"system":"http://terminology.hl7.org/CodeSystem/v3-ActReason","code":"HTEST","display":"test health data"}],"versionId":"1","lastUpdated":"2022-02-13T22:30:20.124+08:00"}}
